import React, { Component } from 'react';
import { addGenreMutation } from '../queries/queries';
import { graphql } from 'react-apollo';

class AddGenre extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	      name: '',
	    };
	  }
	submitForm(e) {
	    e.preventDefault();
	    console.log(this.state);
	    this.props.addGenreMutation({
	      variables: {
	        name: this.state.name,
	      }, 
	      refetchQueries: [{ query: getGenreQuery }]
	    });

	 }
	render() {
		return (
			<form id="add-genre" onSubmit={this.submitForm.bind(this)}>
				<div className="field">
		          <label>Genre name:</label>
		          <input type="text" onChange={ (e) => this.setState({name: e.target.value}) }/>
		        </div>
			</form>
		)
	}
}

export default graphql(addGenreMutation)(AddGenre);
